<script>
  window.addEventListener('load', () => {

      /* Анимация движущейся строки */
    let run_stringsL = document.querySelector('.run_stringsL')
    let run_stringsR = document.querySelector('.run_stringsR')
    let run_stringsLL = document.querySelector('.run_stringsLL')
    let run_stringsRR = document.querySelector('.run_stringsRR')
    let xl = 0
    if (run_stringsL) {
      document.addEventListener('mousemove', move_string)
    }

    function move_string (e) {
      let w = document.documentElement.clientWidth
      let left_side = w / 2
      if (left_side < e.clientX) {
        xl += 7
        run_stringsL.style.transform = `translateX(${xl}px)`
        run_stringsR.style.transform = `translateX(-${xl}px)`
        run_stringsLL.style.transform = `translateX(${xl}px)`
        run_stringsRR.style.transform = `translateX(-${xl}px)`
      } else {
        xl -= 7
        run_stringsL.style.transform = `translateX(-${xl}px)`
        run_stringsR.style.transform = `translateX(${xl}px)`
        run_stringsLL.style.transform = `translateX(-${xl}px)`
        run_stringsRR.style.transform = `translateX(${xl}px)`
      }
    }

      /*if (run_stringsL) {
       // Общая ширина строки
       let width_ticker = run_stringsL.children.length * run_stringsL.children[0].offsetWidth

       let count = 0
       setInterval(() => {
       run_stringsL.style.transform = 'translateX(-' + count + 'px)'
       count = count + 15
       // Если строка закончилась, то сбрасываю сдвиг влево
       if (count > (width_ticker - document.body.clientWidth)) {
       count = 0
       run_stringsL.style.transform = 'translateX(-' + count + 'px)'
       }
       }, 40)
       }*/

      /* Ширина сайта */
    let srcw = document.documentElement.clientWidth
    let full_wrapper = document.querySelector('.full-wrapper')
    full_wrapper.style.width = srcw + 'px'
    //full_wrapper.style.overflow = 'hidden'

      /* Меняю цвет главного квадрата */
    let wrap_eye = document.querySelector('.wrap_eye')
    wrap_eye.addEventListener('mouseenter', change_color_to_violet)
    wrap_eye.addEventListener('mousemove', change_color_to_violet)
    function change_color_to_violet () {
      let wrap_logo_center = document.querySelector('.wrap_logo_center')
      wrap_logo_center.style.background = '#740FC3'
      wrap_logo_center.style.boxShadow = '20px 20px 110px #5D59FF'
    }

    wrap_eye.addEventListener('mouseout', change_color_to_red)
    function change_color_to_red () {
      let wrap_logo_center = document.querySelector('.wrap_logo_center')
      wrap_logo_center.style.background = '#F6173F'
      wrap_logo_center.style.boxShadow = '20px 20px 110px #f9617c'
    }

      /* Анимация глаза */

    document.addEventListener('mousemove', move_eye)

    let scr_w = document.documentElement.clientWidth
    let scr_h = document.documentElement.clientHeight

    function move_eye (e) {

      let x1 = scr_w / 3
      let x2 = (scr_w / 3) * 2
      let x3 = scr_w
      let y1 = scr_h / 3
      let y2 = (scr_h / 3) * 2
      let y3 = scr_h

      let pupil = document.getElementById('pupil')
      pupil.style.transition = `all .2s`

      let dot = document.getElementById('dot')
      dot.style.transition = `all .2s`

      if (e.clientX < x1 && e.clientY < y1) {
        pupil.style.transform = `translate(-9px, -11px)`
        dot.style.top = '3px'
        dot.style.left = '4px'
      } else if (e.clientX > x1 && e.clientX < x2 && e.clientY < y1) {
        pupil.style.transform = `translate(1px, -14px)`
        dot.style.top = '3px'
        dot.style.left = '7px'
      } else if (e.clientX > x2 && e.clientY < y1) {
        pupil.style.transform = `translate(8px, -12px)`
        dot.style.top = '3px'
        dot.style.left = '11px'
      } else if (e.clientX < x1 && e.clientY > y1 && e.clientY < y2) {
        pupil.style.transform = `translate(-12px, -8px)`
        dot.style.top = '7px'
        dot.style.left = '3px'
      } else if (e.clientX > x1 && e.clientX < x2 && e.clientY > y1 && e.clientY < y2) {
        pupil.style.transform = `translate(0px, -9px)`
        dot.style.top = '7px'
        dot.style.left = '7px'
      } else if (e.clientX > x2 && e.clientY < y2 && e.clientY > y1) {
        pupil.style.transform = `translate(13px, -9px)`
        dot.style.top = '7px'
        dot.style.left = '11px'
      } else if (e.clientX < x1 && e.clientY > y2) {
        pupil.style.transform = `translate(-9px, -4px)`
        dot.style.top = '11px'
        dot.style.left = '3px'
      } else if (e.clientX > x1 && e.clientX < x2 && e.clientY > y2) {
        pupil.style.transform = `translate(1px, -4px)`
        dot.style.top = '12px'
        dot.style.left = '8px'
      } else if (e.clientX > x2 && e.clientY > y2) {
        pupil.style.transform = `translate(8px, -5px)`
        dot.style.top = '11px'
        dot.style.left = '11px'
      }

    }
  })
</script>

